import React, { useState, useEffect } from 'react';
import { FormControl, Input } from '@material-ui/core';
import './App.css';
import Message from './Message';
import db from './firebase';
import firebase from './firebase';
import FlipMove from 'react-flip-move';
import {SendIcon} from '@material-ui/core/icons/SendIcon';
import {IconButton} from '@material-ui/core/icons/IconButton';


function App() {

  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [username, setUsername] = useState('');

useEffect(() => {
  db.collection('messages')
  .orderBy('timestamp', 'desc')
  .onSnapshot(snapshot => {
    setMessages(snapshot.docs.map(doc => ({id: doc.id, messages: doc.data()})))
  });
}, [] )

useEffect(() => {
  setUsername(prompt('Please eneter your name'));
}, [] ) //condition


 const sendMessage = (event) => {
  event.preventDefault();

db.collection('messages').add({
  message: input,
  username: username,
  timestamp: firebase.firestore.FieldValue.serverTimestamp()
})
setInput('');
 }

  return (
    <div className="App">
      <img src="https://facebookbrand.com/wp-content/uploads/2018/09/Header-e1538151782912.png?w=100&h=100" atl="Logo"/>
<h1>Hello Message</h1>
<h2>Welcome {username}</h2>



<form className="app__form">
<FormControl>

  <Input placeholder="Enter a Message..." value={input} onChange={event => setInput(event.target.value)}/>
  <IconButton disabled={!input} variant='contained' color='primary' type='submit' onClick={sendMessage}/>
<SendIcon />
<IconButton />
  </FormControl>
  </form>


<FlipMove>
{ 
messages.map(({id, message}) => (
  <Message key={id} username={username} message={message}/>
))
}
</FlipMove>

    </div>
  );
}

export default App;
